#include <bits/stdc++.h>

using namespace std;
using ll = long long;

struct Counter {
    ll even, state, x;
};

struct Generator {
    static constexpr ll m = 1ll<<40;
    static constexpr ll c = 12345;
    ll x = 0x600dcafe;

    Generator& next() {
        x = (x + (x>>20) + c) % m;
        return *this;
    }

    ll upper() {
        return x >> 20;
    }

    bool operator==(Generator const& rhs) const {
        return x == rhs.x;
    }

    bool operator!=(Generator const& rhs) const {
        return x != rhs.x;
    }
};

